mapper.update:
UPDATE BBS
   SET STATE = 0
 WHERE BBS_NO = #{bbsNo}

mapper.insert:

INSERT INTO BBS(
    BBS_NO
  , WRITER
  , TITLE
  , IP
  , CREATED_AT
  , STATE
  , DEPTH
  , GROUP_NO
  , GROUP_ORDER
) VALUES (
    BBS_SEQ.NEXTVAL
  , #{writer}
  , #{title}
  , #{ip}
  , SYSDATE
  , 1
  , #{depth}
  , #{groupNo}
  , #{groupOrder}
)
