mapper.insert:


  SELECT UPLOAD_SEQ.NEXTVAL
    FROM DUAL

INSERT INTO UPLOAD (
    UPLOAD_NO
  , UPLOAD_TITLE
  , UPLOAD_CONTENT
  , CREATED_AT
  , MODIFIED_AT
) VALUES (
    #{uploadNo}
  , #{uploadTitle}
  , #{uploadContent}
  , SYSTIMESTAMP
  , SYSTIMESTAMP
)

INSERT INTO ATTACH (
    ATTACH_NO
  , PATH
  , ORIGIN_NAME
  , FILESYSTEM_NAME
  , DOWNLOAD_COUNT
  , HAS_THUMBNAIL
  , UPLOAD_NO
) VALUES (
    ATTACH_SEQ.NEXTVAL
  , #{path}
  , #{originName}
  , #{filesystemName}
  , 0
  , #{hasThumbnail}
  , #{uploadNo}
)

mapper.select:
SELECT UPLOAD_NO, UPLOAD_TITLE, UPLOAD_CONTENT, CREATED_AT, MODIFIED_AT
  FROM UPLOAD
 WHERE UPLOAD_NO = #{uploadNo}

SELECT ATTACH_NO, PATH, ORIGIN_NAME, FILESYSTEM_NAME, DOWNLOAD_COUNT, HAS_THUMBNAIL, UPLOAD_NO
  FROM ATTACH
 WHERE UPLOAD_NO = #{uploadNo}

SELECT ATTACH_NO, PATH, ORIGIN_NAME, FILESYSTEM_NAME, DOWNLOAD_COUNT, HAS_THUMBNAIL, UPLOAD_NO
  FROM ATTACH
 WHERE ATTACH_NO = #{attachNo}

mapper.update:
UPDATE ATTACH
   SET DOWNLOAD_COUNT = DOWNLOAD_COUNT + 1
 WHERE ATTACH_NO = #{attachNo}

UPDATE UPLOAD
   SET UPLOAD_TITLE = #{uploadTitle}
     , UPLOAD_CONTENT = #{uploadContent}
     , MODIFIED_AT = SYSTIMESTAMP
 WHERE UPLOAD_NO = #{uploadNo}
