mapper.select:
SELECT ID, EMAIL, JOINED_AT
  FROM USER_T
 WHERE ID = #{id}

SELECT ID
  FROM SLEEP_USER_T
 WHERE ID = #{id}

SELECT ID
  FROM LEAVE_USER_T
 WHERE ID = #{id}

SELECT EMAIL
  FROM USER_T
 WHERE EMAIL = #{email}

SELECT EMAIL
  FROM SLEEP_USER_T
 WHERE EMAIL = #{email}

SELECT EMAIL
  FROM LEAVE_USER_T
 WHERE EMAIL = #{email}

mapper.insert:
INSERT INTO USER_T (
    USER_NO
  , ID
  , PW
  , NAME
  , GENDER
  , EMAIL
  , MOBILE
  , BIRTHYEAR
  , BIRTHDATE
  , POSTCODE
  , ROAD_ADDRESS
  , JIBUN_ADDRESS
  , DETAIL_ADDRESS
  , EXTRA_ADDRESS
  , AGREECODE
  , JOINED_AT
  , PW_MODIFIED_AT
  , AUTOLOGIN_ID
  , AUTOLOGIN_EXPIRED_AT
) VALUES (
    USER_SEQ.NEXTVAL
  , #{id}
  , #{pw}
  , #{name}
  , #{gender}
  , #{email}
  , #{mobile}
  , #{birthyear}
  , #{birthdate}
  , #{postcode}
  , #{roadAddress}
  , #{jibunAddress}
  , #{detailAddress}
  , #{extraAddress}
  , #{agreecode}
  , SYSDATE
  , SYSDATE
  , NULL
  , NULL
)

mapper.select:
SELECT ID
  FROM USER_T
 WHERE ID = #{id}
   AND PW = #{pw}

mapper.insert:
INSERT INTO USER_ACCESS_T (
    ID
  , LAST_LOGIN_AT
) VALUES (
    #{id}
  , SYSDATE

)

mapper.update:
UPDATE USER_ACCESS_T
   SET LAST_LOGIN_AT = SYSDATE
 WHERE ID = #{id}

mapper.insert:
INSERT INTO LEAVE_USER_T (
    ID
  , EMAIL
  , JOINED_AT
  , LEAVED_AT
) VALUES (
    #{id}
  , #{email}
  , #{joinedAt}
