SELECT A.COMMENT_NO, A.CONTENT, A.STATE, A.DEPT, A.GROUP_NO, A.CREATED_AT, A.BLOG_NO, A.MEMBER_NO, A.ID, A.NAME FROM (SELECT ROW_NUMBER() OVER(ORDER BY GROUP_NO DESC, DEPTH ASC, COMMENT_NO DESC) AS RN, C.COMMENT_NO, C.CONTENT, C.STATE, C.DEPTH, C.GROUP_NO, C.CREATED_AT, C.BLOG_NO, M.MEMBER_NO, M.ID, M.NAME FROM MEMBER_T M INNER JOIN COMMENT_T C ON M.MEMBER_NO = C.MEMBER_NO WHERE C.BLOG_NO = #{blogNo}) A WHERE A.RN BETWEEN #{begin} AND #{end}
